mapper.insert:
		INSERT 
		  INTO TBL_GOODS_CART
        (
          GOODS_NUMBER, USER_NO, CART_COUNT
        )
        VALUES
        (
          #{goodsNum}, #{userNum}, #{count}
        )

		INSERT 
		  INTO TBL_GOODS_REVIEW
        (
          GOODS_REVIEW_NUM, PAYMENT_NUM, USER_NO, GOODS_NUMBER, REVIEW_DATE, GOODS_REVIEW_CONTENT
        )
        VALUES
        (
          SEQ_TBL_GOODS_REVIEW.NEXTVAL, #{payNum}, #{userNum}, #{goodsNum}, SYSDATE, #{replytext}
        )

mapper.select:
		SELECT
	           A.GOODS_NUMBER
	         , A.USER_NO
    	  FROM TBL_GOODS_CART A
	     WHERE A.GOODS_NUMBER = #{goodsNo}
	       AND A.USER_NO = #{userNo}

		SELECT
	           A.ORDER_NUM
    	  FROM TBL_GOODS_ORDER A
	     WHERE A.GOODS_NUMBER = #{goodsNum}
	       AND A.USER_NO = #{memNum}
	       AND A.PAID_OR_NOT = 'N'

		SELECT
	           A.ORDER_NUM
    	  FROM TBL_GOODS_ORDER A
	     WHERE A.GOODS_NUMBER = #{goodsNum}
	       AND A.USER_NO = #{memNum}
	       AND A.PAID_OR_NOT = 'N'

		SELECT
	           *
    	  FROM TBL_GOODS_LIKE A
	     WHERE A.GOODS_NUMBER = #{goodsNo}
	       AND A.USER_NO = #{userNo}

mapper.update:
	UPDATE TBL_GOODS_REVIEW
	   SET GOODS_REVIEW_CONTENT = #{updateContent}
     WHERE GOODS_REVIEW_NUM = #{reviewNum}

	UPDATE TBL_GOODS_INQUIRY
	   SET GOODS_INQUIRY_CONTENT = #{inquiryUpdateContent}
     WHERE GOODS_INQUIRY_NO = #{inquiryNum}

	UPDATE TBL_GOODS_ORDER A
	   SET A.PAID_OR_NOT = 'Y'
     WHERE A.ORDER_NUM = #{orderNo}

mapper.select:
		SELECT
     	       A.DELIVERY_NUMBER
	      FROM TBL_DELIVERY A
	     WHERE A.PAYMENT_NUM = #{paymentNo}
	       AND A.GOODS_NUMBER = #{goodsNum}
	       AND A.USER_NO = #{memNum}

mapper.update:
	UPDATE TBL_GOODS_ORDER
	   SET ORDER_CANCEL_OR_NOT = 'Y'
     WHERE ORDER_NUM = #{orderNum}

    UPDATE TBL_GOODS_ORDER
	   SET ORDER_COUNT = #{count}
     WHERE ORDER_NUM = #{orderNo}

mapper.delete:
	    DELETE 
          FROM TBL_GOODS_CART
         WHERE GOODS_NUMBER = #{goodsNo}
           AND USER_NO = #{userNo}

mapper.update:
		    UPDATE TBL_GOODS_INQUIRY
           SET GOODS_INQUIRY_YN = 'Y'
         WHERE GOODS_INQUIRY_NO = #{goodsInquiryNum}

mapper.insert:
INSERT INTO USER_REPORT
   (
     REPORT_NO
   , USER_NO
   , REPORT_REASON
   , REPORTED_PERSON
   , REPORT_PLACE_NO
   , REPORT_PLACE_CODE
   , REF_GOODS_NO
   )
   VALUES
   (
     SEQ_REPORT_NO.NEXTVAL
      , #{userNo}
      , #{reportReason}
      , #{reportedPerson}
      , #{reportReplyNo}
      , 2
      , #{placeNo}
   )

mapper.update:
	UPDATE TBL_GOODS A
	   SET A.GOODS_LIKE_COUNT = A.GOODS_LIKE_COUNT + 1
     WHERE A.GOODS_NUMBER = #{goodsNo}

	UPDATE TBL_GOODS A
	   SET A.GOODS_LIKE_COUNT = A.GOODS_LIKE_COUNT - 1
     WHERE A.GOODS_NUMBER = #{goodsNum}
