mapper.insert:
        INSERT 
          INTO USER_INFO A
        (
          A.USER_NO
        , A.USER_ID
        , A.USER_PWD
        , A.USER_NAME
        , A.NICKNAME
        , A.USER_ADDRESS
        , A.EMAIL
        , A.PHONE
        , A.BIRTHDAY
        ) 
		VALUES
		(
		  SEQ_USER_NO.NEXTVAL
		, #{ userId }
		, #{ userPwd }
		, #{ userName }
		, #{ nickname }
		, #{ userAddress }
		, #{ email }
		, #{ phone }
		, #{ birthday }
		)

mapper.select:
        SELECT 
               A.USER_NO
             , A.USER_ID
             , A.USER_PWD
             , A.USER_NAME
             , A.NICKNAME
             , A.USER_ADDRESS
             , A.EMAIL
             , A.PHONE
             , A.BIRTHDAY
             , A.USER_ROLE
             , A.USER_REGISTER
             , A.USER_STATUS
             , A.SPOILER_YN
             , A.MOVIE_REVIEW_YN
             , A.BLACKLIST_DATE
             , A.WITHDRAW_DATE
             , A.REPORT_CUMULATIVE
             , A.SUBSCRIBE_VALIDITY
             , (SELECT
                       C.GENRE_SHAME
                  FROM USER_FAVORITE_GENRE C
                 WHERE C.USER_NO = (SELECT
                                           D.USER_NO
                                      FROM USER_INFO D
                                     
                                     
							           D.USER_NO = #{userNo}
									 
							         
							           AND D.USER_ID = #{userId}
									 
									 
							           AND D.USER_NAME = #{userName}
									 
                                     
                                    )
                   AND C.FAVORITE_GENRE_NO = 1
               ) ACTION
             , (SELECT
                       C.GENRE_SHAME
                  FROM USER_FAVORITE_GENRE C
                 WHERE C.USER_NO = (SELECT
                                           D.USER_NO
                                      FROM USER_INFO D
                                     
                                     
							           D.USER_NO = #{userNo}
									 
							         
							           AND D.USER_ID = #{userId}
									 
									 
							           AND D.USER_NAME = #{userName}
									 
							         
							           AND D.EMAIL = #{email}
									 
                                     
                                    )
                   AND C.FAVORITE_GENRE_NO = 2
               ) FANTASY
             , (SELECT
                       C.GENRE_SHAME
                  FROM USER_FAVORITE_GENRE C
                 WHERE C.USER_NO = (SELECT
                                           D.USER_NO
                                      FROM USER_INFO D
                                     
                                     
							           D.USER_NO = #{userNo}
									 
							         
							           AND D.USER_ID = #{userId}
									 
									 
							           AND D.USER_NAME = #{userName}
									 
							         
							           AND D.EMAIL = #{email}
									 
                                     
                                    )
                   AND C.FAVORITE_GENRE_NO = 3
               ) ROMANCE
             , (SELECT
                       C.GENRE_SHAME
                  FROM USER_FAVORITE_GENRE C
                 WHERE C.USER_NO = (SELECT
                                           D.USER_NO
                                      FROM USER_INFO D
                                     
                                     
							           D.USER_NO = #{userNo}
									 
							         
							           AND D.USER_ID = #{userId}
									 
									 
							           AND D.USER_NAME = #{userName}
									 
							         
							           AND D.EMAIL = #{email}
									 
                                     
                                    )
                   AND C.FAVORITE_GENRE_NO = 4
               ) COMEDY
             , (SELECT
                       C.GENRE_SHAME
                  FROM USER_FAVORITE_GENRE C
                 WHERE C.USER_NO = (SELECT
                                           D.USER_NO
                                      FROM USER_INFO D
                                     
                                     
							           D.USER_NO = #{userNo}
									 
							         
							           AND D.USER_ID = #{userId}
									 
									 
							           AND D.USER_NAME = #{userName}
									 
							         
							           AND D.EMAIL = #{email}
									 
                                     
                                    )
                   AND C.FAVORITE_GENRE_NO = 5
               ) HORROR
          FROM USER_INFO A
           
          
          	A.USER_NO = #{userNo}
		  
          
          	AND A.USER_ID = #{userId}
		  
          
          	AND A.USER_NAME = #{userName}
		  
          
          	AND A.EMAIL = #{email}
		  
            AND A.USER_STATUS='Y'

mapper.update:
UPDATE
       USER_INFO A
   SET A.USER_PWD = #{userPwd}
 WHERE A.USER_ID = #{userId}

UPDATE
       USER_INFO A
   SET A.USER_NAME = #{userName}
     , A.USER_ADDRESS = #{userAddress}
     , A.EMAIL = #{email}
     , A.PHONE = #{phone}
     , A.BIRTHDAY = #{birthday}
 WHERE A.USER_ID = #{userId}

UPDATE
       USER_INFO A
   SET A.USER_PWD = #{userPwd}
 WHERE A.USER_ID = #{userId}

mapper.insert:
INSERT
  INTO PAYMENT A
(
  A.PAYMENT_NO
, A.SUBSCRIPTION_NUMBER
, A.USER_NO
, A.PAYMENT_DATE
, A.PAYMENT_VALIDITY
, A.PAYMENT_PRICE
)
VALUES
(
  SEQ_PAYMENT_NO.NEXTVAL
, #{subscription.number}
, #{userNo}
, SYSDATE
, #{paymentValidity}
, #{paymentPrice}
)

mapper.update:
UPDATE
	   USER_INFO A
   SET A.SUBSCRIBE_VALIDITY = #{paymentValidity}
 WHERE A.USER_NO = #{userNo}
