          AND A.MOVIE_NO = #{ no }

       UPDATE 
              MOVIE_LIKE_DISLIKE A
		  SET A.MOVIE_LIKE_DISLIKE = '싫어요'
		WHERE A.USER_NO = #{ userNo }
          AND A.MOVIE_NO = #{ no }

mapper.delete:
