INSERT 
  INTO MOVIE A
(
   A.MOVIE_NO
 , A.MOVIE_NAME
 , A.MOVIE_INFO
 , A.MOVIE_DIRECTOR
 , A.PRODUCTION_YEAR
 , A.MOVIE_TIME
 , A.WATCH_FEAR
 , A.WATCH_MODIFICATION
 , A.WATCH_DRUG
 , A.WATCH_SENSATIONALITY
 , A.WATCH_SCRIPT
 , A.WATCH_TITLE
 , A.WATCH_VIOLENCE
 , A.RATING_NAME
 , A.GENRE_NAME
 , A.MOVIE_VIDEO_RINK
)
VALUES
(
   SEQ_MOVIE_NO.NEXTVAL
 , #{ name }
 , #{ info }
 , #{ director }
 , #{ productionYear }
 , #{ movieTime }
 , #{ watchFear }
 , #{ watchModification }
 , #{ watchDrug }
 , #{ watchSensationality }
 , #{ watchScript }
 , #{ watchTitle }
 , #{ watchViolence }
 , #{ ratingName }
 , #{ genreName }
 , #{ movieVideoRink }
 )


	 	SELECT
	 		   SEQ_MOVIE_NO.CURRVAL
	      FROM DUAL

        INSERT
          INTO ACTOR A
        (
          A.ACTOR_NO
        , A.MOVIE_NO
        , A.ACTOR_NAME1
        , A.ACTOR_NAME2
        , A.ACTOR_NAME3
        , A.ACTOR_NAME4
        , A.ACTOR_NAME5
        ) 
		VALUES
		(
          SEQ_ACTOR_NO.NEXTVAL
        , #{ movie.no }
        , #{ actorName1 }
        , #{ actorName2 }
        , #{ actorName3 }
        , #{ actorName4 }
        , #{ actorName5 }
		)


	 		SELECT
	 		       SEQ_MOVIE_NO.CURRVAL
	 		  FROM DUAL

        INSERT 
          INTO MOVIE_FILE A
        (
          A.MV_FILE_NO
        , A.MOVIE_NO
        , A.ORIGIN_NAME
        , A.SAVE_NAME
        , A.SAVE_PATH
        ) 
		VALUES
		(
          SEQ_MV_FILE_NO.NEXTVAL
        , #{ movie.no }
        , #{ originName }
        , #{ saveName } 
        , #{ savePath }
		)

mapper.update:
UPDATE
       MOVIE A
   SET A.MOVIE_NAME = #{name}
     , A.MOVIE_INFO = #{info}
     , A.MOVIE_DIRECTOR = #{director}
     , A.PRODUCTION_YEAR = #{productionYear}
     , A.MOVIE_TIME = #{movieTime}
     , A.WATCH_FEAR = #{watchFear}
     , A.WATCH_MODIFICATION = #{watchModification}
     , A.WATCH_DRUG = #{watchDrug}
     , A.WATCH_SENSATIONALITY = #{watchSensationality}
     , A.WATCH_SCRIPT = #{watchScript}
     , A.WATCH_TITLE = #{watchTitle}
     , A.WATCH_VIOLENCE = #{watchViolence}
     , A.RATING_NAME = #{ratingName}
     , A.GENRE_NAME = #{genreName}
     , A.MOVIE_VIDEO_RINK = #{movieVideoRink}
  WHERE A.MOVIE_NO = #{no}

UPDATE
