   SET 
       A.ACTOR_NAME1 = #{actorName1}
     , A.ACTOR_NAME2 = #{actorName2}
     , A.ACTOR_NAME3 = #{actorName3}
     , A.ACTOR_NAME4 = #{actorName4}
     , A.ACTOR_NAME5 = #{actorName5}
 WHERE A.MOVIE_NO = #{no}

