mapper.insert:
	INSERT
	  INTO USER_BOARD A
	     (
	       A.POST_NO
          ,A.USER_NO
		  ,A.POST_TITLE
		  ,A.POST_CONTENT
          ,A.CATEGORY
	     )
	VALUES
	     (
	     SEQ_USER_BOARD.NEXTVAL
	   , #{userNo}
	   , #{title}
	   , #{content}
	   , #{category}
	     )

	INSERT
      INTO USER_REPORT A
 		 (
  		   A.REPORT_NO 
         , A.USER_NO
 	     , A.REPORT_REASON
 	     , A.OTHER_REASON
 	     , A.REPORTED_PERSON
         , A.REPORT_PLACE_NO
         , A.REPORT_PLACE_CODE
         )VALUES
         (
 	       SEQ_REPORT_NO.NEXTVAL
	     , #{userNo}
	     , #{reason}
         , #{otherReason}
         , #{reportedPerson}
         , #{placeNo}
         , 1
   )

	INSERT
      INTO BOARD_REPLY A
 		 (
  		   A.COMMENT_NO 
         , A.POST_NO
         , A.REPLY_CONTENT
         , A.USER_NO
         )VALUES
         (
 	       SEQ_BOARD_REPLY.NEXTVAL
	     , #{postNo}
	     , #{content}
         , #{userNo}
	     )

	INSERT
      INTO REPLYOF A
 		 (
  		   A.REPLYOF_NO 
         , A.REF_REPLY_NO
         , A.REPLYOF_CONTENT
         , A.USER_NO
         , A.REF_BOARD_NO
         )VALUES
         (
 	       SEQ_REPLYOF_NO.NEXTVAL
	     , #{refNo}
	     , #{replyOfContent}
         , #{userNo}
         , #{refBoardNo}
	     )

mapper.update:
		UPDATE
   		       USER_BOARD A
           SET A.POST_TITLE = #{title}
             , A.POST_CONTENT = #{content}
             , A.CATEGORY = #{category}
         WHERE A.POST_NO = #{no}

		UPDATE
   		       BOARD_REPLY A
           SET 
               A.REPLY_CONTENT = #{modifyContent}
         WHERE A.COMMENT_NO = #{replyNo}

		UPDATE
   		       REPLYOF A
           SET 
               A.REPLYOF_CONTENT = #{modifyReplyOfContent}
         WHERE A.REPLYOF_NO = #{replyOfNo}

